sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);
		// If the testdate is before rightnow, return true, else return false
		return testdate.before(rightnow);
	},

	isDateToday: function(strDate){
		//Create GlideDateTime objects for parameter and current date
		var rightNowDate = new GlideDateTime().getLocalDate();
		var parameterDate = new GlideDateTime(strDate).getLocalDate();

		//Returns true or false depending on whether the two GlideDateTime objects have the same date.
		//This happens when the function returns 0 (a false value)
		return !rightNowDate.compareTo(parameterDate);
	},

    type: 'NeedItUtils'
};
